import {LOGIN_USER,
    USER_LOADING,
    LOGOUT_USER,
    REGISTER_USER,
    GET_AUTH_USER,
    AUTH_ERRORS}  from "../constant/Actions-types"

    const initialState ={
        token:localStorage.getItem('token'),
        user:null,
        isAuth:false,
        isLoading:true,
        msg:null
    }

    const authReducer = (state =initialState,{type,payload})=>{
        switch (type) {
            case REGISTER_USER:
            case LOGIN_USER:
                localStorage.setItem('token',payload.token)
                return{
                    ...state,
                    isLoading:false,
                    isAuth:true,
                    ...payload
                }
        
            default:
                return state
        }
    }

    export default authReducer
